const scrollElements=document.querySelectorAll(".scroll-animation"),elementInView=(e,n=1)=>{return e.getBoundingClientRect().top<.8*window.innerHeight},elementOutofView=e=>{return e.getBoundingClientRect().top>.8*window.innerHeight},displayScrollElement=e=>{e.classList.add("on")},hideScrollElement=e=>{e.classList.remove("on")},handleScrollAnimation=()=>{scrollElements.forEach(e=>{elementInView(e)?displayScrollElement(e):elementOutofView(e)&&hideScrollElement(e)})};window.addEventListener("scroll",()=>{handleScrollAnimation()}),window.onload=(()=>{handleScrollAnimation()});